mapper.insert:
insert user_base_information(
uname,upasswd,uphone,uidentity
uclubId,ustudioId,ucity,ucity,
uschool,upicture
)
values(
#{uname},#{upasswd},#{uphone},#{uidentity}
#{uclubId},#{ustudioId},#{ucity},#{ucity},
#{uschool},#{upicture}
)

mapper.update:
update user_base_information
set upasswd = #{upasswd}
where uid = #{uid}

update user_base_information
uname=#{uname}, upasswd=#{upasswd}, uphone=#{uphone}, uidentity=#{uidentity}, uclubId=#{uclubId}, ustudioId=#{ustudioId}, ucity=#{ucity}, uschool=#{uschool}, upicture=#{upicture},
where uid = #{uid}
